import React from "react";
import styles from "./books.module.css";
import Book from "../../components/Book/Book";

const Books = ()=>{
    //ovu datu sam ja hardkodirala ovdje. u praksi (i kako cemo mi to izvesti) ove podatke cemo mi dobiti sa backenda tj nasih mikroservisa
    const bookList = [
        {
            title: "Catcher In The Rye",
            author:{
                name: "J. D.",
                surname: "Salinger"
            },
            cover: "https://drive.google.com/drive/folders/1Ld5fT9bmy9z_BhYFPbjq_0AGuijd7-U8?usp=sharing/tmnt_universe_20.png",
            summary:"The Catcher in the Rye is a story by J. D. Salinger, partially published in serial form in 1945–1946 and as a novel in 1951. It was originally intended for adults, but is often read by adolescents for its themes of angst and alienation, and as a critique on superficiality in society. "
        },
        {
            title:"Amilina knjizurina",
            author:{
                name:"Amila",
                surname: "allsks"
            },
            cover: "https://drive.google.com/open?id=1jkgmuNwmcoQa1hIvUe1usN--W596QxQQ",
            summary:"tralalala"
        }
    ];
    //ovdje imate nesto sto ce nam svima sigurno trebati - funkcija map
    //ima funkciju petlje, samo radi puno brze i generalno preferira se u odnosu na obicnu for petlju
    return(
        <div className={styles.container}>
            {bookList.map(book =>
                <div className={styles.item} key={book.title}>
                    <Book author={book.author} cover={book.cover} summary={book.summary} title={book.title}/>
                </div>
            )}
        </div>
    );
}

export default Books;
