import React, {useEffect, useState} from "react";
import axios from "axios";
import styles from "./Book.module.css";

//slika se ne moze prikazati zbog CORS-a, kasnije cemo to kontati
const Book = ({title, author, cover, summary})=>{
    const [image, setImage] = useState("");


    return(
        <div className="card text-white bg-info mb-3" style={{maxWidth: "250px"}}>
            <div className="card-header">{author.name} {author.surname}</div>
            <div className="card-body">
                <h4 className="card-title">{title}</h4>
                <p className="card-text">{summary}</p>
                <img className={styles.image} src={cover} alt={"naslovnica knjige"}/>
            </div>
        </div>
    );
}

export default Book;
