import React, {useEffect, useState} from "react";
import styles from "./Book.module.css";
import axios from "axios";


//slika se ne moze prikazati zbog CORS-a, kasnije cemo to kontati
const Book = ({title, author, cover, summary})=>{
    const [image, setImage] = useState("");

    useEffect(() => {
        axios.get(cover, {
            headers: {
                "Content-type": "application/json",
                'Access-Control-Allow-Origin': '*'
            },
            proxy: {
                host: "https://cors-anywhere.herokuapp.com/",
            }
        }).then(function (response) {
            setImage(response.data);
            console.log('response is : ' + response.data);
        }).catch(function (error) {
            if (error.response) {
                console.log(error.response.headers);
            }
            else if (error.request) {
                console.log(error.request);
            }
            else {
                console.log(error.message);
            }
            console.log(error.config);
        });
    }, []);

    return(
        <div className="card text-white bg-info mb-3" style={{maxWidth: "250px"}}>
            <div className="card-header">{author.name} {author.surname}</div>
            <div className="card-body">
                <h4 className="card-title">{title}</h4>
                <p className="card-text">{summary}</p>
                <img  src={cover} alt={"naslovnica knjige"}/>
            </div>
        </div>
    );
}

export default Book;
