import React, {useState} from "react";
import "./ColorChangingButton.module.css";


const ColorChangingButton = ()=>{
    const colors = ["#9097d7", "#e2d489", "#a21e21", "#a9124c", "#52c436", "#d45629", "#553a36"];
    const [currentColor, setCurrentColor] = useState(colors[0]);
    return(
        <button type="button" className="btn btn-primary"
                style={{backgroundColor:currentColor, border:currentColor}}
                onClick={()=>handleColorChange(currentColor, setCurrentColor, colors)}>Click me!</button>
    );
}

//ovdje mozemo izmijeniti state komponente. Za strebere poput mene - ovo ovdje je duboko kopiranje (sa tp-a), dakle kada sa setCurrentColor promijenite
//stanje currentColor, ono ce biti izmijenjeno i u ColorChangingButton, ne samo u onClick funkciji (iako je ona skroz izvan)

const handleColorChange = (currentColor, setCurrentColor, colors)=>{
    const prevColor = currentColor;
    let randomBroj = Math.floor(Math.random() * colors.length);
    setCurrentColor(colors[randomBroj]);
    console.log("Promijenjena boja iz " + prevColor + " u " + currentColor);
}

export default ColorChangingButton;
