import React from "react";
import styles from "./InfoCard.module.css";

//ovo je primjer veoma jednostavne komponente koja ima malo stajlinga i neke staticne informacije

//props su pomocne informacije koje parent komponenta salje child komponenti
//u angularu i vuejs child komponenta moze roditelju da salje propove takodjer - u reactu toga nema, to se rjesava na neke okolo kole nacine ako se za tim ukaze potreba

const InfoCard = ({children, name, surname})=>{
    return(
        <div className={styles.container}>
            <div className="card border-primary mb-3" style={{maxWidth: "250px"}}>
                <div className="card-header">Some title</div>
                <div className="card-body">
                    <h4 className="card-title">{name} {surname}</h4>
                    <p className="card-text">{children}</p>
                </div>
            </div>
        </div>

    );
}

export default InfoCard;
